const books = () => {
      //   fetch('http://localhost:3000/books')
  //   .then(resp => resp.json())
  //   .then(books => {
  //     books.map(book => dispatch(addBook(book)))
  //   })
}

const booksReducer = (state = {books: []}, action) => {
    switch (action.type) {
        case 'ADD_BOOK': 
            return {books: [...state.books, action.book]}
        default:
            return state
    }
}

export default booksReducer
