        //         'Accept': 'application/json'
        //     },
        //     body: JSON.stringify(this.state)
        // }).then(resp => resp.json())
        // .then(book => {
        //     this.props.addBook(book)
        //     this.setState({
        //         title: "",
        //         author: "",
        //         status: ""
        //     })
        // })
    }

    return (
        <form onSubmit={this.submitHandler} className='new-book-form'>
            <input type='text' name='title' onChange={(e) => setTitle(e.target.value)} value={this.state.title} placeholder='Title'/><br/>
            <input type='text' name='author' onChange={(e) => setAuthor(e.target.value)} value={this.state.author} placeholder='Author'/><br/>
            <select name='status' id='status' onChange={(e) => setStatus(e.target.value)} >
                <option selected>-- Select Read Status --</option>
                <option value='read'>Read</option>
                <option value='reading'>Reading</option>
                <option value='to_read'>To Read</option>
            </select><br/>                
            <input type='submit'/>
        </form>
    )
}

const mapDispatchToProps = dispatch => {
    return {
        addBook: book => dispatch(addBook(book))
    }
}

