const books = () => {
    return fetch('http://localhost:3000/books')
    .then(resp => resp.json())
    .then(books => books)
}

const booksReducer = (state = {books: books()}, action) => {
    switch (action.type) {
        case 'ADD_BOOK': 
            return {books: [...state.books, action.book]}
        default:
            return state
    }
}

export default booksReducer
